import React, { useState, useEffect } from 'react';
import './ChatSearch.css';

const ChatSearch = ({ chatHistory, onSearchResults, isVisible, onClose }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [searchType, setSearchType] = useState('all'); // all, questions, answers
  const [sortBy, setSortBy] = useState('newest'); // newest, oldest, relevance

  useEffect(() => {
    if (searchTerm.trim() === '') {
      setSearchResults([]);
      onSearchResults([]);
      return;
    }

    const filtered = chatHistory.filter(item => {
      const matchesType = searchType === 'all' || 
                         (searchType === 'questions' && item.type === 'question') ||
                         (searchType === 'answers' && item.type === 'answer');
      
      const matchesSearch = item.content.toLowerCase().includes(searchTerm.toLowerCase());
      
      return matchesType && matchesSearch;
    });

    // ÿ™ÿ±ÿ™Ÿäÿ® ÿßŸÑŸÜÿ™ÿßÿ¶ÿ¨
    const sorted = [...filtered].sort((a, b) => {
      if (sortBy === 'newest') {
        return new Date(b.timestamp) - new Date(a.timestamp);
      } else if (sortBy === 'oldest') {
        return new Date(a.timestamp) - new Date(b.timestamp);
      } else { // relevance
        const aRelevance = countMatches(a.content, searchTerm);
        const bRelevance = countMatches(b.content, searchTerm);
        return bRelevance - aRelevance;
      }
    });

    setSearchResults(sorted);
    onSearchResults(sorted);
  }, [searchTerm, searchType, sortBy, chatHistory, onSearchResults]);

  const countMatches = (text, term) => {
    const regex = new RegExp(term.toLowerCase(), 'g');
    return (text.toLowerCase().match(regex) || []).length;
  };

  const highlightText = (text, term) => {
    if (!term) return text;
    
    const regex = new RegExp(`(${term})`, 'gi');
    const parts = text.split(regex);
    
    return parts.map((part, index) => 
      regex.test(part) ? <mark key={index}>{part}</mark> : part
    );
  };

  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    const now = new Date();
    const diff = now - date;
    const days = Math.floor(diff / (1000 * 60 * 60 * 24));
    
    if (days === 0) {
      return 'ÿßŸÑŸäŸàŸÖ ' + date.toLocaleTimeString('ar-SA', { 
        hour: '2-digit', 
        minute: '2-digit' 
      });
    } else if (days === 1) {
      return 'ÿ£ŸÖÿ≥ ' + date.toLocaleTimeString('ar-SA', { 
        hour: '2-digit', 
        minute: '2-digit' 
      });
    } else if (days < 7) {
      return `ŸÖŸÜÿ∞ ${days} ÿ£ŸäÿßŸÖ`;
    } else {
      return date.toLocaleDateString('ar-SA');
    }
  };

  const clearSearch = () => {
    setSearchTerm('');
    setSearchResults([]);
    onSearchResults([]);
  };

  if (!isVisible) return null;

  return (
    <div className="chat-search-overlay">
      <div className="chat-search-container">
        <div className="search-header">
          <h3>üîç ÿßŸÑÿ®ÿ≠ÿ´ ŸÅŸä ÿßŸÑŸÖÿ≠ÿßÿØÿ´ÿßÿ™</h3>
          <button className="close-btn" onClick={onClose}>‚úï</button>
        </div>

        {/* ÿ¥ÿ±Ÿäÿ∑ ÿßŸÑÿ®ÿ≠ÿ´ */}
        <div className="search-bar">
          <div className="search-input-container">
            <input
              type="text"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              placeholder="ÿßÿ®ÿ≠ÿ´ ŸÅŸä ÿßŸÑŸÖÿ≠ÿßÿØÿ´ÿßÿ™..."
              className="search-input"
              autoFocus
            />
            {searchTerm && (
              <button className="clear-search-btn" onClick={clearSearch}>
                ‚úï
              </button>
            )}
          </div>
        </div>

        {/* ŸÅŸÑÿßÿ™ÿ± ÿßŸÑÿ®ÿ≠ÿ´ */}
        <div className="search-filters">
          <div className="filter-group">
            <label>ŸÜŸàÿπ ÿßŸÑÿ®ÿ≠ÿ´:</label>
            <select 
              value={searchType} 
              onChange={(e) => setSearchType(e.target.value)}
              className="filter-select"
            >
              <option value="all">ÿßŸÑŸÉŸÑ</option>
              <option value="questions">ÿßŸÑÿ£ÿ≥ÿ¶ŸÑÿ© ŸÅŸÇÿ∑</option>
              <option value="answers">ÿßŸÑÿ•ÿ¨ÿßÿ®ÿßÿ™ ŸÅŸÇÿ∑</option>
            </select>
          </div>

          <div className="filter-group">
            <label>ÿ™ÿ±ÿ™Ÿäÿ®:</label>
            <select 
