import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import { getAIResponse } from './services/openai';
import AdminPanel from './components/AdminPanel';
import Analytics from './components/Analytics';
import './App.css';

// الصفحة الرئيسية للمحادثة
function ChatPage() {
  const [question, setQuestion] = useState('');
  const [loading, setLoading] = useState(false);
  const [chatHistory, setChatHistory] = useState([]);
  const [feedback, setFeedback] = useState({});

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!question.trim()) return;

    setLoading(true);
    
    try {
      // إضافة السؤال للتاريخ
      const newQuestion = { type: 'question', content: question, timestamp: new Date() };
      setChatHistory(prev => [...prev, newQuestion]);
      
      // الحصول على إجابة من الذكي الاصطناعي
      const aiResponse = await getAIResponse(question);
      
      // إضافة الإجابة للتاريخ
      const newResponse = { type: 'answer', content: aiResponse, timestamp: new Date() };
      setChatHistory(prev => [...prev, newResponse]);
      
      setQuestion(''); // مسح النص بعد الإرسال
      
    } catch (error) {
      console.error('خطأ:', error);
      const errorResponse = { 
        type: 'answer', 
        content: 'عذراً، حدث خطأ. يرجى المحاولة مرة أخرى.', 
        timestamp: new Date() 
      };
      setChatHistory(prev => [...prev, errorResponse]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <main className="app-main">
      {/* منطقة المحادثة */}
      <div className="chat-container">
        {chatHistory.length === 0 ? (
          <div className="welcome-message">
            <h3>مرحباً بك! 👋</h3>
            <p>اسأل أي سؤال عن الجامعة وسأساعدك في الإجابة</p>
            <div className="example-questions">
              <small>أمثلة على الأسئلة:</small>
              <ul>
                <li onClick={() => setQuestion('كيف أسجل المواد؟')}>كيف أسجل المواد؟</li>
                <li onClick={() => setQuestion('ما هي أوقات المكتبة؟')}>ما هي أوقات المكتبة؟</li>
                <li onClick={() => setQuestion('معلومات عن التخصصات')}>معلومات عن التخصصات</li>
              </ul>
            </div>
          </div>
        ) : (
          <div className="chat-history">
            {chatHistory.map((item, index) => (
              <div key={index} className={`chat-message ${item.type}`}>
                <div className="message-content">
                  {item.content}
                </div>
                <div className="message-time">
                  {item.timestamp.toLocaleTimeString('ar-SA', { 
                    hour: '2-digit', 
                    minute: '2-digit' 
                  })}
                </div>
              </div>
            ))}
          </div>
        )}
        
        {loading && (
          <div className="loading-message">
            <div className="loading-dots">
              <span></span>
              <span></span>
              <span></span>
            </div>
            <p>جاري البحث عن الإجابة...</p>
          </div>
        )}
      </div>

      {/* نموذج السؤال */}
      <form onSubmit={handleSubmit} className="question-form">
        <div className="input-group">
          <textarea
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            placeholder="اكتب سؤالك هنا..."
            className="question-input"
            rows="3"
            disabled={loading}
          />
          <button 
            type="submit" 
            className="submit-button"
            disabled={loading || !question.trim()}
          >
            {loading ? 'جاري الإرسال...' : 'أرسل السؤال'}
          </button>
        </div>
      </form>
    </main>
  );
}

// شريط التنقل
function Navigation() {
  const location = useLocation();
  
  return (
    <nav className="app-nav">
      <Link 
        to="/" 
        className={`nav-link ${location.pathname === '/' ? 'active' : ''}`}
      >
        💬 المساعد الذكي
      </Link>
      <Link 
        to="/admin" 
        className={`nav-link ${location.pathname === '/admin' ? 'active' : ''}`}
      >
        📊 لوحة التحكم
      </Link>
      <Link 
        to="/analytics" 
        className={`nav-link ${location.pathname === '/analytics' ? 'active' : ''}`}
      >
        📈 التحليلات
      </Link>
    </nav>
  );
}

// صفحة التحليلات - تستدعي المكون الجديد
const AnalyticsPage = () => <Analytics />;

function App() {
  return (
    <Router>
      <div className="App">
        <header className="app-header">
          <h1>Ask MEU</h1>
          <p>مساعدك الذكي للحياة الجامعية في جامعة الشرق الأوسط</p>
          <Navigation />
        </header>

        <Routes>
          <Route path="/" element={<ChatPage />} />
          <Route path="/admin" element={<AdminPanel />} />
          <Route path="/analytics" element={<AnalyticsPage />} />
        </Routes>

        <footer className="app-footer">
          <p>جامعة الشرق الأوسط - Middle East University</p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
