import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import { getAIResponse } from './services/openai';
import AdminPanel from './components/AdminPanel';
import Analytics from './components/Analytics';
import './App.css';

// ÿßŸÑÿµŸÅÿ≠ÿ© ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ© ŸÑŸÑŸÖÿ≠ÿßÿØÿ´ÿ©
function ChatPage() {
  const [question, setQuestion] = useState('');
  const [loading, setLoading] = useState(false);
  const [chatHistory, setChatHistory] = useState([]);
  const [feedback, setFeedback] = useState({});

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!question.trim()) return;

    setLoading(true);
    
    try {
      // ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑÿ≥ÿ§ÿßŸÑ ŸÑŸÑÿ™ÿßÿ±ŸäÿÆ
      const questionId = Date.now();
      const newQuestion = { 
        id: questionId,
        type: 'question', 
        content: question, 
        timestamp: new Date() 
      };
      setChatHistory(prev => [...prev, newQuestion]);
      
      // ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿ•ÿ¨ÿßÿ®ÿ© ŸÖŸÜ ÿßŸÑÿ∞ŸÉŸä ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä
      const aiResponse = await getAIResponse(question);
      
      // ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑÿ•ÿ¨ÿßÿ®ÿ© ŸÑŸÑÿ™ÿßÿ±ŸäÿÆ
      const newResponse = { 
        id: questionId + 1,
        type: 'answer', 
        content: aiResponse, 
        timestamp: new Date(),
        questionId: questionId,
        showRating: true
      };
      setChatHistory(prev => [...prev, newResponse]);
      
      setQuestion(''); // ŸÖÿ≥ÿ≠ ÿßŸÑŸÜÿµ ÿ®ÿπÿØ ÿßŸÑÿ•ÿ±ÿ≥ÿßŸÑ
      
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£:', error);
      const errorResponse = { 
        id: Date.now(),
        type: 'answer', 
        content: 'ÿπÿ∞ÿ±ÿßŸãÿå ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£. Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ.', 
        timestamp: new Date(),
        showRating: false
      };
      setChatHistory(prev => [...prev, errorResponse]);
    } finally {
      setLoading(false);
    }
  };

  // Ÿàÿ∏ŸäŸÅÿ© ÿ™ŸÇŸäŸäŸÖ ÿßŸÑÿ•ÿ¨ÿßÿ®ÿ©
  const rateAnswer = (answerId, rating) => {
    setFeedback(prev => ({
      ...prev,
      [answerId]: rating
    }));
    
    // ÿ•ÿÆŸÅÿßÿ° ÿ£ÿ≤ÿ±ÿßÿ± ÿßŸÑÿ™ŸÇŸäŸäŸÖ ÿ®ÿπÿØ ÿßŸÑÿ™ŸÇŸäŸäŸÖ
    setChatHistory(prev => 
      prev.map(item => 
        item.id === answerId 
          ? { ...item, showRating: false, rated: true, rating: rating }
          : item
      )
    );
  };

  // ÿßŸÇÿ™ÿ±ÿßÿ≠ÿßÿ™ ÿßŸÑÿ£ÿ≥ÿ¶ŸÑÿ© ÿßŸÑÿ≥ÿ±Ÿäÿπÿ©
  const quickSuggestions = [
    'ŸÉŸäŸÅ ÿ£ÿ≥ÿ¨ŸÑ ÿßŸÑŸÖŸàÿßÿØÿü',
    'ŸÖÿß ŸáŸä ÿ£ŸàŸÇÿßÿ™ ÿßŸÑŸÖŸÉÿ™ÿ®ÿ©ÿü',
    'ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿπŸÜ ÿßŸÑÿ™ÿÆÿµÿµÿßÿ™',
    'ÿßŸÑÿ±ÿ≥ŸàŸÖ ÿßŸÑÿØÿ±ÿßÿ≥Ÿäÿ©',
    'ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑÿßÿ™ÿµÿßŸÑ',
    'ŸÜÿ∏ÿßŸÖ ÿßŸÑÿßŸÖÿ™ÿ≠ÿßŸÜÿßÿ™'
  ];

  return (
    <main className="app-main">
      {/* ŸÖŸÜÿ∑ŸÇÿ© ÿßŸÑŸÖÿ≠ÿßÿØÿ´ÿ© */}
      <div className="chat-container">
        {chatHistory.length === 0 ? (
          <div className="welcome-message">
            <h3>ŸÖÿ±ÿ≠ÿ®ÿßŸã ÿ®ŸÉ! üëã</h3>
            <p>ÿßÿ≥ÿ£ŸÑ ÿ£Ÿä ÿ≥ÿ§ÿßŸÑ ÿπŸÜ ÿßŸÑÿ¨ÿßŸÖÿπÿ© Ÿàÿ≥ÿ£ÿ≥ÿßÿπÿØŸÉ ŸÅŸä ÿßŸÑÿ•ÿ¨ÿßÿ®ÿ©</p>
            <div className="example-questions">
              <small>ÿ£ŸÖÿ´ŸÑÿ© ÿπŸÑŸâ ÿßŸÑÿ£ÿ≥ÿ¶ŸÑÿ©:</small>
              <ul>
                <li onClick={() => setQuestion('ŸÉŸäŸÅ ÿ£ÿ≥ÿ¨ŸÑ ÿßŸÑŸÖŸàÿßÿØÿü')}>ŸÉŸäŸÅ ÿ£ÿ≥ÿ¨ŸÑ ÿßŸÑŸÖŸàÿßÿØÿü</li>
                <li onClick={() => setQuestion('ŸÖÿß ŸáŸä ÿ£ŸàŸÇÿßÿ™ ÿßŸÑŸÖŸÉÿ™ÿ®ÿ©ÿü')}>ŸÖÿß ŸáŸä ÿ£ŸàŸÇÿßÿ™ ÿßŸÑŸÖŸÉÿ™ÿ®ÿ©ÿü</li>
                <li onClick={() => setQuestion('ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿπŸÜ ÿßŸÑÿ™ÿÆÿµÿµÿßÿ™')}>ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿπŸÜ ÿßŸÑÿ™ÿÆÿµÿµÿßÿ™</li>
              </ul>
            </div>
          </div>
        ) : (
          <div className="chat-history">
            {chatHistory.map((item, index) => (
              <div key={index} className={`chat-message ${item.type}`}>
                <div className="message-content">
                  {item.content}
                </div>
                <div className="message-footer">
                  <div className="message-time">
                    {item.timestamp.toLocaleTimeString('ar-SA', { 
                      hour: '2-digit', 
                      minute: '2-digit' 
                    })}
                  </div>
                  
                  {/* ŸÜÿ∏ÿßŸÖ ÿ™ŸÇŸäŸäŸÖ ÿßŸÑÿ•ÿ¨ÿßÿ®ÿßÿ™ */}
                  {item.type === 'answer' && item.showRating && (
                    <div className="rating-section">
                      <span className="rating-text">ŸáŸÑ ŸÉÿßŸÜÿ™ ÿßŸÑÿ•ÿ¨ÿßÿ®ÿ© ŸÖŸÅŸäÿØÿ©ÿü</span>
                      <div className="rating-buttons">
                        <button 
                          className="rating-btn positive"
                          onClick={() => rateAnswer(item.id, 'positive')}
                          title="ŸÖŸÅŸäÿØÿ©"
                        >
                          üëç
                        </button>
                        <button 
                          className="rating-btn negative"
                          onClick={() => rateAnswer(item.id, 'negative')}
                          title="ÿ∫Ÿäÿ± ŸÖŸÅŸäÿØÿ©"
                        >
                          üëé
                        </button>
                      </div>
                    </div>
                  )}
                  
                  {/* ÿπÿ±ÿ∂ ÿßŸÑÿ™ŸÇŸäŸäŸÖ ÿ®ÿπÿØ ÿßŸÑÿ™ŸÇŸäŸäŸÖ */}
                  {item.rated && (
                    <div className="rating-result">
                      <span className="rating-thanks">
                        ÿ¥ŸÉÿ±ÿßŸã ŸÑÿ™ŸÇŸäŸäŸÖŸÉ! {item.rating === 'positive' ? 'üëç' : 'üëé'}
                      </span>
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}
        
        {loading && (
          <div className="loading-message">
            <div className="loading-dots">
              <span></span>
              <span></span>
              <span></span>
            </div>
            <p>ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ®ÿ≠ÿ´ ÿπŸÜ ÿßŸÑÿ•ÿ¨ÿßÿ®ÿ©...</p>
          </div>
        )}
      </div>

      {/* ŸÜŸÖŸàÿ∞ÿ¨ ÿßŸÑÿ≥ÿ§ÿßŸÑ */}
      <form onSubmit={handleSubmit} className="question-form">
        <div className="input-group">
          <textarea
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            placeholder="ÿßŸÉÿ™ÿ® ÿ≥ÿ§ÿßŸÑŸÉ ŸáŸÜÿß..."
            className="question-input"
            rows="3"
            disabled={loading}
          />
          <button 
            type="submit" 
            className="submit-button"
            disabled={loading || !question.trim()}
          >
            {loading ? 'ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ•ÿ±ÿ≥ÿßŸÑ...' : 'ÿ£ÿ±ÿ≥ŸÑ ÿßŸÑÿ≥ÿ§ÿßŸÑ'}
          </button>
        </div>
        
        {/* ÿßŸÇÿ™ÿ±ÿßÿ≠ÿßÿ™ ÿ≥ÿ±Ÿäÿπÿ© */}
        {chatHistory.length > 0 && (
          <div className="quick-suggestions">
            <div className="suggestions-header">
              <span>üí° ÿßŸÇÿ™ÿ±ÿßÿ≠ÿßÿ™ ÿ£ÿÆÿ±Ÿâ:</span>
            </div>
            <div className="suggestions-list">
              {quickSuggestions
                .filter(suggestion => suggestion !== question)
                .slice(0, 3)
                .map((suggestion, index) => (
                <button
                  key={index}
                  type="button"
                  className="suggestion-btn"
                  onClick={() => setQuestion(suggestion)}
                  disabled={loading}
                >
                  {suggestion}
                </button>
              ))}
            </div>
          </div>
        )}
      </form>
    </main>
  );
}

