import OpenAI from 'openai';

// إعداد OpenAI client
const openai = new OpenAI({
  apiKey: 'demo-key', // مؤقت للتطوير
  dangerouslyAllowBrowser: true
});

// وظيفة للحصول على إجابة تجريبية
export const getAIResponse = async (question) => {
  // إجابات تجريبية للتطوير
  const responses = {
    'مرحبا': 'مرحباً بك في Ask MEU! كيف يمكنني مساعدتك اليوم؟',
    'التسجيل': 'يمكنك التسجيل للمواد من خلال نظام التسجيل الإلكتروني في الجامعة.',
    'المكتبة': 'مكتبة الجامعة مفتوحة من الساعة 8 صباحاً حتى 8 مساءً.',
    'default': 'شكراً لك على سؤالك! فريق الدعم سيجيبك قريباً.'
  };

  // محاكاة تأخير الشبكة
  await new Promise(resolve => setTimeout(resolve, 1000));

  // البحث عن إجابة مناسبة
  for (let key in responses) {
    if (question.includes(key)) {
      return responses[key];
    }
  }
  
  return responses.default;
};
