import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom';
import './App.css';
import { getAIResponse } from './services/openai'; // ‚úÖ ÿ™ŸÖ ÿ™ÿµÿ≠Ÿäÿ≠ ÿßŸÑÿßÿ≥ŸÖ
import AdminPanel from './components/AdminPanel';
import Analytics from './components/Analytics';
import ChatSearch from './components/ChatSearch';

function App() {
  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<ChatPage />} />
          <Route path="/admin" element={<AdminPanel />} />
          <Route path="/analytics" element={<Analytics />} />
        </Routes>
      </div>
    </Router>
  );
}

function Navigation() {
  return (
    <nav className="main-nav">
      <div className="nav-container">
        <div className="nav-logo">
          <span className="logo-icon">üéì</span>
          <span className="logo-text">Ask MEU</span>
        </div>
        <div className="nav-links">
          <Link to="/" className="nav-link">
            <span className="nav-icon">üí¨</span>
            ÿßŸÑÿØÿ±ÿØÿ¥ÿ©
          </Link>
          <Link to="/analytics" className="nav-link">
            <span className="nav-icon">üìä</span>
            ÿßŸÑÿ™ÿ≠ŸÑŸäŸÑÿßÿ™
          </Link>
          <Link to="/admin" className="nav-link">
            <span className="nav-icon">‚öôÔ∏è</span>
            ÿßŸÑÿ•ÿØÿßÿ±ÿ©
          </Link>
        </div>
      </div>
    </nav>
  );
}

function ChatPage() {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [showSearch, setShowSearch] = useState(false);
  const [searchResults, setSearchResults] = useState([]);
  const [chatHistory, setChatHistory] = useState([]);
  const messagesEndRef = useRef(null);
  const navigate = useNavigate();

  // ÿ™ÿ≠ŸàŸäŸÑ ÿßŸÑÿ±ÿ≥ÿßÿ¶ŸÑ ÿ•ŸÑŸâ ÿ™ÿßÿ±ŸäÿÆ ŸÇÿßÿ®ŸÑ ŸÑŸÑÿ®ÿ≠ÿ´
  useEffect(() => {
    const history = [];
    messages.forEach((message, index) => {
      if (message.type === 'user') {
        history.push({
          id: `q-${index}`,
          type: 'question',
          content: message.content,
          timestamp: message.timestamp || new Date().toISOString()
        });
      } else if (message.type === 'bot') {
        history.push({
          id: `a-${index}`,
          type: 'answer',
          content: message.content,
          timestamp: message.timestamp || new Date().toISOString()
        });
      }
    });
    setChatHistory(history);
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // ÿ£ŸÖÿ´ŸÑÿ© ÿßŸÑÿ£ÿ≥ÿ¶ŸÑÿ© ÿ≠ÿ≥ÿ® ÿßŸÑÿ™ÿµŸÜŸäŸÅ
  const questionExamples = {
    all: [
      "ŸÉŸäŸÅ ÿ£ÿ≥ÿ¨ŸÑ ŸÅŸä ÿßŸÑŸÖŸàÿßÿØÿü",
      "ŸÖÿß ŸáŸä ÿßŸÑÿ™ÿÆÿµÿµÿßÿ™ ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©ÿü",
      "ÿ£ŸäŸÜ ÿ™ŸÇÿπ ÿßŸÑŸÖŸÉÿ™ÿ®ÿ©ÿü",
      "ŸÖÿ™Ÿâ ÿ™ÿ®ÿØÿ£ ÿßŸÑÿßŸÖÿ™ÿ≠ÿßŸÜÿßÿ™ÿü"
    ],
    registration: [
      "ŸÉŸäŸÅ ÿ£ÿ≥ÿ¨ŸÑ ŸÅŸä ÿßŸÑŸÖŸàÿßÿØ ÿßŸÑÿØÿ±ÿßÿ≥Ÿäÿ©ÿü",
      "ŸÖÿß ŸáŸä ÿ±ÿ≥ŸàŸÖ ÿßŸÑÿ™ÿ≥ÿ¨ŸäŸÑÿü",
      "ŸÉŸäŸÅ ÿ£ÿ≠ÿµŸÑ ÿπŸÑŸâ ŸÖŸÜÿ≠ÿ© ÿØÿ±ÿßÿ≥Ÿäÿ©ÿü",
      "ŸÖÿ™Ÿâ ŸÅÿ™ÿ±ÿ© ÿßŸÑÿ≥ÿ≠ÿ® ŸàÿßŸÑÿ•ÿ∂ÿßŸÅÿ©ÿü"
    ],
    majors: [
      "ŸÖÿß ŸáŸä ÿ™ÿÆÿµÿµÿßÿ™ ŸÉŸÑŸäÿ© ÿ™ŸÉŸÜŸàŸÑŸàÿ¨Ÿäÿß ÿßŸÑŸÖÿπŸÑŸàŸÖÿßÿ™ÿü",
      "ŸÉŸÖ ÿ≥ŸÜÿ© ÿØÿ±ÿßÿ≥ÿ© ÿßŸÑŸáŸÜÿØÿ≥ÿ©ÿü",
      "ŸÖÿß ŸÖÿ™ÿ∑ŸÑÿ®ÿßÿ™ ÿßŸÑŸÇÿ®ŸàŸÑ ŸÅŸä ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ£ÿπŸÖÿßŸÑÿü",
      "ŸáŸÑ ŸäŸàÿ¨ÿØ ÿ™ÿÆÿµÿµ ÿ∑ÿ®ÿü"
    ],
    facilities: [
      "ÿ£ŸäŸÜ ÿ™ŸÇÿπ ÿßŸÑŸÖŸÉÿ™ÿ®ÿ© ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©ÿü",
      "ŸÖÿß ŸáŸä ÿ£ŸàŸÇÿßÿ™ ÿπŸÖŸÑ ÿßŸÑŸÉÿßŸÅÿ™Ÿäÿ±Ÿäÿßÿü",
      "ŸáŸÑ ÿ™Ÿàÿ¨ÿØ ŸÖÿÆÿ™ÿ®ÿ±ÿßÿ™ ÿ≠ÿßÿ≥Ÿàÿ®ÿü",
      "ÿ£ŸäŸÜ ÿ£ÿ¨ÿØ ŸÖŸàÿßŸÇŸÅ ÿßŸÑÿ≥Ÿäÿßÿ±ÿßÿ™ÿü"
    ],
    exams: [
      "ŸÖÿ™Ÿâ ÿ™ÿ®ÿØÿ£ ÿßŸÑÿßŸÖÿ™ÿ≠ÿßŸÜÿßÿ™ ÿßŸÑŸÜŸáÿßÿ¶Ÿäÿ©ÿü",
      "ŸÉŸäŸÅ Ÿäÿ™ŸÖ ÿ™Ÿàÿ≤Ÿäÿπ ÿßŸÑÿØÿ±ÿ¨ÿßÿ™ÿü",
      "ŸÖÿß ŸáŸà ŸÜÿ∏ÿßŸÖ ÿßŸÑÿ™ŸÇÿØŸäÿ±ÿßÿ™ÿü",
      "ŸÉŸäŸÅ ÿ£ÿ™ÿ∏ŸÑŸÖ ŸÖŸÜ ÿØÿ±ÿ¨ÿ©ÿü"
    ],
    contact: [
      "ŸÖÿß ŸáŸà ÿ±ŸÇŸÖ Ÿáÿßÿ™ŸÅ ÿßŸÑÿ¨ÿßŸÖÿπÿ©ÿü",
      "ÿ£ŸäŸÜ ŸäŸÇÿπ ŸÖŸÉÿ™ÿ® ÿßŸÑŸÇÿ®ŸàŸÑ ŸàÿßŸÑÿ™ÿ≥ÿ¨ŸäŸÑÿü",
      "ŸÉŸäŸÅ ÿ£ÿ™ŸàÿßÿµŸÑ ŸÖÿπ ÿßŸÑÿØÿπŸÖ ÿßŸÑŸÅŸÜŸäÿü",
      "ŸÖÿß ŸáŸà ÿπŸÜŸàÿßŸÜ ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸäÿü"
    ]
  };

  const categories = [
    { id: 'all', name: 'ÿßŸÑŸÉŸÑ', icon: 'üìö' },
    { id: 'registration', name: 'ÿßŸÑÿ™ÿ≥ÿ¨ŸäŸÑ', icon: 'üìù' },
    { id: 'majors', name: 'ÿßŸÑÿ™ÿÆÿµÿµÿßÿ™', icon: 'üéì' },
    { id: 'facilities', name: 'ÿßŸÑŸÖÿ±ÿßŸÅŸÇ', icon: 'üè¢' },
    { id: 'exams', name: 'ÿßŸÑÿßŸÖÿ™ÿ≠ÿßŸÜÿßÿ™', icon: 'üìä' },
    { id: 'contact', name: 'ÿßŸÑÿ™ŸàÿßÿµŸÑ', icon: 'üìû' }
  ];

  const handleSubmit = async (question = null) => {
    const messageText = question || inputValue.trim();
    if (!messageText) return;

    const userMessage = {
      type: 'user',
      content: messageText,
      timestamp: new Date().toISOString()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputValue('');
    setIsLoading(true);

    try {
      const response = await getAIResponse(messageText); // ‚úÖ ÿ™ŸÖ ÿ™ÿµÿ≠Ÿäÿ≠ ÿßÿ≥ŸÖ ÿßŸÑÿØÿßŸÑÿ©
      const botMessage = {
        type: 'bot',
        content: response,
        timestamp: new Date().toISOString(),
        rating: null
      };
      setMessages(prev => [...prev, botMessage]);
    } catch (error) {
      console.error('Error:', error);
      const errorMessage = {
        type: 'bot',
        content: 'ÿπÿ∞ÿ±ÿßŸãÿå ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑŸÜÿ∏ÿßŸÖ. Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ ŸÑÿßÿ≠ŸÇÿßŸã. üòî',
        timestamp: new Date().toISOString(),
        rating: null
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleRating = (messageIndex, rating) => {
    setMessages(prev => prev.map((msg, index) => 
      index === messageIndex ? { ...msg, rating } : msg
    ));
  };

  const handleExampleClick = (example) => {
    handleSubmit(example);
  };

  const currentExamples = questionExamples[selectedCategory];

  // ŸÖÿπÿßŸÑÿ¨ ŸÜÿ™ÿßÿ¶ÿ¨ ÿßŸÑÿ®ÿ≠ÿ´
  const handleSearchResults = (results) => {
    setSearchResults(results);
  };

  // ÿ•ÿ∫ŸÑÿßŸÇ ÿßŸÑÿ®ÿ≠ÿ´
  const handleCloseSearch = () => {
    setShowSearch(false);
    setSearchResults([]);
  };

  return (
    <div className="chat-page">
      <Navigation />
      
      <div className="chat-container">
        {/* ÿ±ÿ£ÿ≥ ÿßŸÑÿØÿ±ÿØÿ¥ÿ© ŸÖÿπ ÿ≤ÿ± ÿßŸÑÿ®ÿ≠ÿ´ */}
        <div className="chat-header">
          <div className="header-content">
            <div className="header-text">
              <h1>ŸÖÿ≥ÿßÿπÿØ ÿßŸÑÿ≠ÿ±ŸÖ ÿßŸÑÿ¨ÿßŸÖÿπŸä MEU ü§ñ</h1>
              <p>ÿßÿ≥ÿ£ŸÑ ÿπŸÜ ÿ£Ÿä ÿ¥Ÿäÿ° Ÿäÿ™ÿπŸÑŸÇ ÿ®ÿ¨ÿßŸÖÿπÿ© ÿßŸÑÿ¥ÿ±ŸÇ ÿßŸÑÿ£Ÿàÿ≥ÿ∑!</p>
            </div>
            <div className="header-actions">
              <button 
                className="search-toggle-btn"
                onClick={() => setShowSearch(true)}
                title="ÿßŸÑÿ®ÿ≠ÿ´ ŸÅŸä ÿßŸÑŸÖÿ≠ÿßÿØÿ´ÿßÿ™"
              >
                üîç ÿßŸÑÿ®ÿ≠ÿ´
              </button>
              <button 
                className="clear-chat-btn"
                onClick={() => {
                  setMessages([]);
                  setChatHistory([]);
                }}
                title="ŸÖÿ≥ÿ≠ ÿßŸÑŸÖÿ≠ÿßÿØÿ´ÿ©"
              >
                üóëÔ∏è ŸÖÿ≥ÿ≠
              </button>
            </div>
          </div>
        </div>

        {/* ŸÅŸÑÿ™ÿ± ÿßŸÑÿ™ÿµŸÜŸäŸÅÿßÿ™ */}
        <div className="categories-filter">
          {categories.map(category => (
            <button
              key={category.id}
              className={`category-btn ${selectedCategory === category.id ? 'active' : ''}`}
              onClick={() => setSelectedCategory(category.id)}
            >
              <span className="category-icon">{category.icon}</span>
              <span className="category-name">{category.name}</span>
            </button>
          ))}
        </div>

        {/* ŸÖŸÜÿ∑ŸÇÿ© ÿßŸÑÿ±ÿ≥ÿßÿ¶ŸÑ */}
        <div className="messages-container">
          {messages.length === 0 ? (
            <div className="welcome-screen">
              <div className="welcome-content">
                <div className="welcome-icon">üéì</div>
                <h2>ŸÖÿ±ÿ≠ÿ®ÿßŸã ÿ®ŸÉ ŸÅŸä ŸÖÿ≥ÿßÿπÿØ MEU ÿßŸÑÿ∞ŸÉŸä!</h2>
                <p>ÿßÿÆÿ™ÿ± ŸÅÿ¶ÿ© Ÿàÿßÿ∂ÿ∫ÿ∑ ÿπŸÑŸâ ÿ≥ÿ§ÿßŸÑÿå ÿ£Ÿà ÿßŸÉÿ™ÿ® ÿ≥ÿ§ÿßŸÑŸÉ ŸÖÿ®ÿßÿ¥ÿ±ÿ©:</p>
                
                <div className="example-questions">
                  <h3>ÿ£ÿ≥ÿ¶ŸÑÿ© ŸÖŸÇÿ™ÿ±ÿ≠ÿ© - {categories.find(c => c.id === selectedCategory)?.name}:</h3>
                  <div className="examples-grid">
                    {currentExamples.map((example, index) => (
                      <button
                        key={index}
                        className="example-btn"
                        onClick={() => handleExampleClick(example)}
                      >
                        {example}
                      </button>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          ) : (
            <>
              {messages.map((message, index) => (
                <div key={index} className={`message ${message.type}`}>
                  <div className="message-content">
                    <div className="message-text">
                      {message.content}
                    </div>
                    {message.type === 'bot' && (
                      <div className="message-actions">
                        <div className="rating-buttons">
                          <button
                            className={`rating-btn ${message.rating === 'up' ? 'active' : ''}`}
                            onClick={() => handleRating(index, message.rating === 'up' ? null : 'up')}
                            title="ŸÖŸÅŸäÿØ"
                          >
                            üëç
                          </button>
                          <button
                            className={`rating-btn ${message.rating === 'down' ? 'active' : ''}`}
                            onClick={() => handleRating(index, message.rating === 'down' ? null : 'down')}
                            title="ÿ∫Ÿäÿ± ŸÖŸÅŸäÿØ"
                          >
                            üëé
                          </button>
                        </div>
                        <div className="message-time">
                          {new Date(message.timestamp).toLocaleTimeString('ar-SA', {
                            hour: '2-digit',
                            minute: '2-digit'
                          })}
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              ))}
              
              {isLoading && (
                <div className="message bot loading">
                  <div className="message-content">
                    <div className="typing-indicator">
                      <span></span>
                      <span></span>
                      <span></span>
                    </div>
                  </div>
                </div>
              )}
            </>
          )}
          <div ref={messagesEndRef} />
        </div>

        {/* ŸÖÿ±ÿ®ÿπ ÿßŸÑÿ•ÿØÿÆÿßŸÑ */}
        <div className="input-container">
          <form onSubmit={(e) => { e.preventDefault(); handleSubmit(); }}>
            <div className="input-group">
              <input
                type="text"
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
                placeholder="ÿßŸÉÿ™ÿ® ÿ≥ÿ§ÿßŸÑŸÉ ŸáŸÜÿß..."
                disabled={isLoading}
                className="chat-input"
              />
              <button
                type="submit"
                disabled={isLoading || !inputValue.trim()}
                className="send-button"
              >
                {isLoading ? '‚è≥' : 'üì§'}
              </button>
            </div>
          </form>
          
          {/* ÿßŸÇÿ™ÿ±ÿßÿ≠ÿßÿ™ ÿ≥ÿ±Ÿäÿπÿ© */}
          {messages.length > 0 && (
            <div className="quick-suggestions">
              <div className="suggestions-title">ÿßŸÇÿ™ÿ±ÿßÿ≠ÿßÿ™ ÿ≥ÿ±Ÿäÿπÿ©:</div>
              <div className="suggestions-list">
                {currentExamples.slice(0, 2).map((suggestion, index) => (
                  <button
                    key={index}
                    className="suggestion-btn"
                    onClick={() => handleExampleClick(suggestion)}
                  >
                    {suggestion}
                  </button>
                ))}
              </div>
            </div>
          )}
        </div>
      </div>

      {/* ŸÖŸÉŸàŸÜ ÿßŸÑÿ®ÿ≠ÿ´ */}
      <ChatSearch
        chatHistory={chatHistory}
        onSearchResults={handleSearchResults}
        isVisible={showSearch}
        onClose={handleCloseSearch}
      />
    </div>
  );
}

export default App;
