import React, { useState } from 'react';
import { getAIResponse } from './services/openai';
import './App.css';

function App() {
  const [question, setQuestion] = useState('');
  const [loading, setLoading] = useState(false);
  const [chatHistory, setChatHistory] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!question.trim()) return;

    setLoading(true);
    
    try {
      // ุฅุถุงูุฉ ุงูุณุคุงู ููุชุงุฑูุฎ
      const newQuestion = { type: 'question', content: question, timestamp: new Date() };
      setChatHistory(prev => [...prev, newQuestion]);
      
      // ุงูุญุตูู ุนูู ุฅุฌุงุจุฉ ูู ุงูุฐูู ุงูุงุตุทูุงุนู
      const aiResponse = await getAIResponse(question);
      
      // ุฅุถุงูุฉ ุงูุฅุฌุงุจุฉ ููุชุงุฑูุฎ
      const newResponse = { type: 'answer', content: aiResponse, timestamp: new Date() };
      setChatHistory(prev => [...prev, newResponse]);
      
      setQuestion(''); // ูุณุญ ุงููุต ุจุนุฏ ุงูุฅุฑุณุงู
      
    } catch (error) {
      console.error('ุฎุทุฃ:', error);
      const errorResponse = { 
        type: 'answer', 
        content: 'ุนุฐุฑุงูุ ุญุฏุซ ุฎุทุฃ. ูุฑุฌู ุงููุญุงููุฉ ูุฑุฉ ุฃุฎุฑู.', 
        timestamp: new Date() 
      };
      setChatHistory(prev => [...prev, errorResponse]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>Ask MEU</h1>
        <p>ูุณุงุนุฏู ุงูุฐูู ููุญูุงุฉ ุงูุฌุงูุนูุฉ ูู ุฌุงูุนุฉ ุงูุดุฑู ุงูุฃูุณุท</p>
      </header>

      <main className="app-main">
        {/* ููุทูุฉ ุงููุญุงุฏุซุฉ */}
        <div className="chat-container">
          {chatHistory.length === 0 ? (
            <div className="welcome-message">
              <h3>ูุฑุญุจุงู ุจู! ๐</h3>
              <p>ุงุณุฃู ุฃู ุณุคุงู ุนู ุงูุฌุงูุนุฉ ูุณุฃุณุงุนุฏู ูู ุงูุฅุฌุงุจุฉ</p>
            </div>
          ) : (
            <div className="chat-history">
              {chatHistory.map((item, index) => (
                <div key={index} className={`chat-message ${item.type}`}>
                  <div className="message-content">
                    {item.content}
                  </div>
                </div>
              ))}
            </div>
          )}
          
          {loading && (
            <div className="loading-message">
              <p>ุฌุงุฑู ุงูุจุญุซ ุนู ุงูุฅุฌุงุจุฉ...</p>
            </div>
          )}
        </div>

        {/* ูููุฐุฌ ุงูุณุคุงู */}
        <form onSubmit={handleSubmit} className="question-form">
          <textarea
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            placeholder="ุงูุชุจ ุณุคุงูู ููุง..."
            className="question-input"
          />
          <button type="submit" disabled={loading || !question.trim()}>
            {loading ? 'ุฌุงุฑู ุงูุฅุฑุณุงู...' : 'ุฃุฑุณู ุงูุณุคุงู'}
          </button>
        </form>
      </main>
    </div>
  );
}

export default App;
