import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import { getAIResponse } from './services/openai';
import AdminPanel from './components/AdminPanel';
import './App.css';

// ÿßŸÑÿµŸÅÿ≠ÿ© ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ© ŸÑŸÑŸÖÿ≠ÿßÿØÿ´ÿ©
function ChatPage() {
  const [question, setQuestion] = useState('');
  const [loading, setLoading] = useState(false);
  const [chatHistory, setChatHistory] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!question.trim()) return;

    setLoading(true);
    
    try {
      // ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑÿ≥ÿ§ÿßŸÑ ŸÑŸÑÿ™ÿßÿ±ŸäÿÆ
      const newQuestion = { type: 'question', content: question, timestamp: new Date() };
      setChatHistory(prev => [...prev, newQuestion]);
      
      // ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿ•ÿ¨ÿßÿ®ÿ© ŸÖŸÜ ÿßŸÑÿ∞ŸÉŸä ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä
      const aiResponse = await getAIResponse(question);
      
      // ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑÿ•ÿ¨ÿßÿ®ÿ© ŸÑŸÑÿ™ÿßÿ±ŸäÿÆ
      const newResponse = { type: 'answer', content: aiResponse, timestamp: new Date() };
      setChatHistory(prev => [...prev, newResponse]);
      
      setQuestion(''); // ŸÖÿ≥ÿ≠ ÿßŸÑŸÜÿµ ÿ®ÿπÿØ ÿßŸÑÿ•ÿ±ÿ≥ÿßŸÑ
      
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£:', error);
      const errorResponse = { 
        type: 'answer', 
        content: 'ÿπÿ∞ÿ±ÿßŸãÿå ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£. Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ.', 
        timestamp: new Date() 
      };
      setChatHistory(prev => [...prev, errorResponse]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <main className="app-main">
      {/* ŸÖŸÜÿ∑ŸÇÿ© ÿßŸÑŸÖÿ≠ÿßÿØÿ´ÿ© */}
      <div className="chat-container">
        {chatHistory.length === 0 ? (
          <div className="welcome-message">
            <h3>ŸÖÿ±ÿ≠ÿ®ÿßŸã ÿ®ŸÉ! üëã</h3>
            <p>ÿßÿ≥ÿ£ŸÑ ÿ£Ÿä ÿ≥ÿ§ÿßŸÑ ÿπŸÜ ÿßŸÑÿ¨ÿßŸÖÿπÿ© Ÿàÿ≥ÿ£ÿ≥ÿßÿπÿØŸÉ ŸÅŸä ÿßŸÑÿ•ÿ¨ÿßÿ®ÿ©</p>
            <div className="example-questions">
              <small>ÿ£ŸÖÿ´ŸÑÿ© ÿπŸÑŸâ ÿßŸÑÿ£ÿ≥ÿ¶ŸÑÿ©:</small>
              <ul>
                <li>ŸÉŸäŸÅ ÿ£ÿ≥ÿ¨ŸÑ ÿßŸÑŸÖŸàÿßÿØÿü</li>
                <li>ŸÖÿß ŸáŸä ÿ£ŸàŸÇÿßÿ™ ÿßŸÑŸÖŸÉÿ™ÿ®ÿ©ÿü</li>
                <li>ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿπŸÜ ÿßŸÑÿ™ÿÆÿµÿµÿßÿ™</li>
              </ul>
            </div>
          </div>
        ) : (
          <div className="chat-history">
            {chatHistory.map((item, index) => (
              <div key={index} className={`chat-message ${item.type}`}>
                <div className="message-content">
                  {item.content}
                </div>
                <div className="message-time">
                  {item.timestamp.toLocaleTimeString('ar-SA', { 
                    hour: '2-digit', 
                    minute: '2-digit' 
                  })}
                </div>
              </div>
            ))}
          </div>
        )}
        
        {loading && (
          <div className="loading-message">
            <div className="loading-dots">
              <span></span>
              <span></span>
              <span></span>
            </div>
            <p>ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ®ÿ≠ÿ´ ÿπŸÜ ÿßŸÑÿ•ÿ¨ÿßÿ®ÿ©...</p>
          </div>
        )}
      </div>

      {/* ŸÜŸÖŸàÿ∞ÿ¨ ÿßŸÑÿ≥ÿ§ÿßŸÑ */}
      <form onSubmit={handleSubmit} className="question-form">
        <div className="input-group">
          <textarea
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            placeholder="ÿßŸÉÿ™ÿ® ÿ≥ÿ§ÿßŸÑŸÉ ŸáŸÜÿß..."
            className="question-input"
            rows="3"
            disabled={loading}
          />
          <button 
            type="submit" 
            className="submit-button"
            disabled={loading || !question.trim()}
          >
            {loading ? 'ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ•ÿ±ÿ≥ÿßŸÑ...' : 'ÿ£ÿ±ÿ≥ŸÑ ÿßŸÑÿ≥ÿ§ÿßŸÑ'}
          </button>
        </div>
      </form>
    </main>
  );
}

// ÿ¥ÿ±Ÿäÿ∑ ÿßŸÑÿ™ŸÜŸÇŸÑ
function Navigation() {
  const location = useLocation();
  
  return (
    <nav className="app-nav">
      <Link 
        to="/" 
        className={`nav-link ${location.pathname === '/' ? 'active' : ''}`}
      >
        üí¨ ÿßŸÑŸÖÿ≥ÿßÿπÿØ ÿßŸÑÿ∞ŸÉŸä
      </Link>
      <Link 
        to="/admin" 
        className={`nav-link ${location.pathname === '/admin' ? 'active' : ''}`}
      >
        üìä ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ
      </Link>
      <Link 
        to="/analytics" 
